package com.goodhealth.algorithm.LintCode_Array;

/**
 * @author 24663
 * @date 2018年10月6日
 * @Description   
 * 116. 跳跃游戏
给出一个非负整数数组，你最初定位在数组的第一个位置。　　　
数组中的每个元素代表你在那个位置可以跳跃的最大长度。　　　　
判断你是否能到达数组的最后一个位置。
样例
A = [2,3,1,1,4]，返回 true.
A = [3,2,1,0,4]，返回 false.
 */
public class TI_116 {
	public static void main(String[] args) {
		TI_116 iTi_116=new TI_116();
		int[] A={2,3,1,1,4};
		int[] B={5,8,3,0,6,7,9,6,3,4,5,2,0,6,2,6,7,10,8,0};
		System.out.println(iTi_116.can(B));
	}
	
	//使用动态规划
    public boolean canJump(int[] A) {
    	//bool[i]表示是否可以从位置0到位置i
    	boolean[] bool=new boolean[A.length];
        bool[0]=true;
    	for (int j = 1; j <bool.length; j++) {
        	for (int i= 0; i<j ; i++) {
        		//是否可以到达j取决于是否存在i小于j，使得0可以到达i，i又可以到j
				if (bool[i]&&A[i]>=j-i) {
					bool[j]=true;
					break;
				}
			}
		}
		return bool[A.length-1];
    }
  
    //使用贪心
    public boolean can(int[] A){
    	//维护一个代表从位置0出发可以到达的最远位置的值farthest（不包括本身的位置）
    	int farthest=A[0];
        for (int i = 1; i < A.length; i++) {
            if (i <= farthest && A[i] + i >= farthest) {
                farthest = A[i] + i;
            }
        }
    	 return farthest >= A.length - 1;
    }

}

